mapper.update:
update board
set title=#{title}, content=#{content}
where write_no=#{write_no}

update board
set hit=hit+1
where write_no=#{write_no}


create sequence board_seq
increment by 1
start with ${start}

update board
set write_no=write_no-1
where write_no>#{write_no}

update board_image
set write_no=write_no-1
where write_no>#{write_no}

update board_like
set write_no=write_no-1
where write_no>#{write_no}

update board
set like_count=like_count-1
where write_no=#{write_no}

update board
set like_count=like_count+1
where write_no=#{write_no}
